import{_ as l,o as i,c,a as n}from"./app-53705635.js";const t={},r=n('<h3 id="基本极限公式" tabindex="-1"><a class="header-anchor" href="#基本极限公式" aria-hidden="true">#</a> 基本极限公式：</h3><ol><li><strong>常数极限：</strong><ul><li>lim (c) = c，其中 c 是常数</li></ul></li><li><strong>幂函数极限：</strong><ul><li>lim (x^n) = a^n，其中 n 是常数，a 是实数</li><li>lim (a^x) = a^c，其中 a 是实数，c 是常数</li></ul></li><li><strong>指数函数极限：</strong><ul><li>lim (e^x) = e^c，其中 c 是常数</li></ul></li><li><strong>三角函数极限：</strong><ul><li>lim (sin(x)) = sin(c)，其中 c 是常数</li><li>lim (cos(x)) = cos(c)，其中 c 是常数</li><li>lim (tan(x)) = tan(c)，其中 c 是常数</li></ul></li><li><strong>反三角函数极限：</strong><ul><li>lim (arcsin(x)) = arcsin(c)，其中 c 是常数</li><li>lim (arccos(x)) = arccos(c)，其中 c 是常数</li><li>lim (arctan(x)) = arctan(c)，其中 c 是常数</li></ul></li></ol><h3 id="极限运算法则" tabindex="-1"><a class="header-anchor" href="#极限运算法则" aria-hidden="true">#</a> 极限运算法则：</h3><ol><li><strong>极限的和差法则：</strong><ul><li>lim (f(x) + g(x)) = lim (f(x)) + lim (g(x))</li><li>lim (f(x) - g(x)) = lim (f(x)) - lim (g(x))</li></ul></li><li><strong>极限的乘法法则：</strong><ul><li>lim (f(x) * g(x)) = lim (f(x)) * lim (g(x))</li></ul></li><li><strong>极限的除法法则：</strong><ul><li>lim (f(x) / g(x)) = (lim (f(x))) / (lim (g(x)))，其中 lim (g(x)) ≠ 0</li></ul></li><li><strong>复合函数的极限：</strong><ul><li>lim (f(g(x))) = lim (f(u))，其中 u = g(x)</li></ul></li><li><strong>连续函数的极限：</strong><ul><li>若 f(x) 连续于 c 处，那么 lim (f(x)) = f(c)</li></ul></li></ol>',4),s=[r];function o(a,e){return i(),c("div",null,s)}const _=l(t,[["render",o],["__file","常用极限公式.html.vue"]]);export{_ as default};
